package Package_B_R;

public class JavaExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k = 1;
		for (int j = 0; j < 4; j++) {

			for (int i = 1; i <= 4 - j; i++) {

				System.out.print(k);
				System.out.print("\t");
				k++;

			}
			System.out.println();

		}

	
	
	
	
	
	
	
	for(int m=1;m<5;m++) {
		for(int n=1;n<=m;n++){
			System.out.print(n);
			System.out.print("\t");
			
		}
		System.out.println();	
			
		}
		
		
}
}
